


var abstractService = require("./abstractService")

var HomeScreenService = (function () {
    function HomeScreenService(packageRepository, launchWithPersistentConsole) {
        this.packageRepository = packageRepository;
        this.launchWithPersistentConsole = launchWithPersistentConsole;
    }
    HomeScreenService.prototype.index = function (aRequest, aResponse) {
        aResponse.render('index', {
            title: 'Intel Tizen IVI Proof of Concept',
            services: abstractService.AttachedServices,
            applications: this.packageRepository.getInstalledApps(),
            launchWithPersistentConsole: this.launchWithPersistentConsole
        });
    };
    return HomeScreenService;
})();
exports.HomeScreenService = HomeScreenService;
